    fun calc(f: String, s: String): Int {
        val e = "$f loves $s"
        val n = mutableListOf<Int>()
        val x = mutableListOf<Char>()
        for(i in e.indices) {
            if (e[i] != ' ') {
                if (!x.contains(e[i])) {
                    x.add(e[i])
                    n.add(match(e, e[i].toString()))
                }
            }
        }
        var ci = 0
        var cl: Int = n.size - 1
        while(n.size > 2) {
            if (ci != cl) {
                val o = n[ci] + n[cl]
                n.removeAt(cl)
                n.removeAt(ci)
                n.add(ci, o)
                cl = n.size - 1
            }
            ci = (ci + 1) % n.size
        }
        var q = ((n.first().toString()) + (n.last().toString())).toInt()
        if(q > 100) {
            q = 100
        }
        return q
    }

    private fun match(s: String, p: String): Int {
        var index = 0
        var count = 0
        while (true) {
            index = s.indexOf(p, index)
            index += if (index != -1) {
                count++
                p.length
            } else {
                return count
            }
        }
    }
